import React, { useEffect, useState } from 'react'
import { Route, useParams, useRouteMatch } from 'react-router-dom'
import { busca } from '../api/api'

import '../assets/css/blog.css'
import ListaCategorias from '../components/ListaCategorias'
import ListaPost from '../components/ListaPost'


const Categoria = () => {

    const {id} = useParams()
    const {path} = useRouteMatch() // O useRouteMatch vai ser responsável por me mostrar o path, se eu estou em “bem-estar” ou “comportamento”. Seria tipo isso no path: '/categoria/bem-estar'
    const [subCategorias, setSubCategorias] = useState([])

    useEffect(() => {
        busca(`/categorias/${id}`, (categoria) => {setSubCategorias(categoria)})
    }, [])

    return(
        <>
            <div className="container">
                <h2 className="titulo-pagina">Pet Notícias</h2>
            </div>

            <ListaCategorias />
            <Route exact path={`${path}/`}>
                <ListaPost url={`/posts?categoria=${id}`} />
            </Route>
        </>
    )
}

export default Categoria
